export default {
  data: function data() {
    return {
      btn_show: false
    };
  },
  methods: {
    isShowBtn: function isShowBtn() {
      if (!!document.documentElement.scrollTop && document.documentElement.scrollTop > 50) {
        this.btn_show = true;
      } else if (document.documentElement.scrollTop == 0) {
        this.btn_show = false;
      }
    }
  },
  mounted: function mounted() {
    window.addEventListener("scroll", this.isShowBtn);
  }
};
